maze, movement = input.split('\n\n')
maze = [list(row) for row in maze.split('\n')]

M, N = len(maze), len(maze[0])

robot_start = (0, 0)
for i in range(M):
    for j in range(N):
        if maze[i][j] == '@':
            robot_start = (i, j)
            maze[i][j] = '.'
            break

for c in movement:
    i, j = robot_start
    dir = (0, 0)
    if c == '<':
        dir = (0, -1)
    elif c == '>':
        dir = (0, 1)
    elif c == '^':
        dir = (-1, 0)
    elif c == 'v':
        dir = (1, 0)

    i += dir[0]
    j += dir[1]

    if i < 0 or i >= M or j < 0 or j >= N or maze[i][j] == '#':
        continue  # assert False, 'Robot hit a wall'

    if maze[i][j] == 'O':
        # push boxes in dir
        freespace, boxes = 0, 0
        while maze[i + dir[0] * boxes][j + dir[1] * boxes] == 'O':
            boxes += 1

        if maze[i + dir[0] * boxes][j + dir[1] * boxes] == '.':
            maze[i + dir[0] * boxes][j + dir[1] * boxes] = 'O'
            maze[i][j] = '.'
        else:
            continue
    robot_start = (i, j)


print('\n'.join([''.join(row) for row in maze]))

sum_wall_coords = 0
for i in range(M):
    for j in range(N):
        if maze[i][j] == 'O':
            sum_wall_coords += i * 100 + j

print(sum_wall_coords)
